public class ex_switch {
	static int key = 2;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		switch(key){
		case 1:
			System.out.println("case1");
			break;
		case 2:
			System.out.println("case2");
			break; 
		default:
			System.out.println("default");
			break;
			}

			
	
	}
}